import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { createDog } from "../../redux/actions/actions";
import Temperaments from "../Temperaments/Temperaments";

const CreateDog = () => {
  const [input, setInput] = useState({
    name: "",
    height_min: "",
    height_max: "",
    weight_min: "",
    weight_max: "",
    life_span: "",
    image: "",
    temperament: [],
  });

  function validate(input2) {
    let errors = {};
  }

  function handleInput(e) {
    e.preventDefault();
    setInput((input) => ({
      ...input,
      [e.target.name]: e.target.value,
    }));
  }
  console.log(input);
  function handleInputTemps(e) {
    e.preventDefault();
    setInput((input) => ({
      ...input,
      temperament: [...input.temperament, e.target.value],
    }));
  }
  console.log(input.temperament);
  const dispatch = useDispatch();

  function handleOnSubmit(e) {
    e.preventDefault();
    // console.log(input);
    console.log(e);
    dispatch(createDog(input));
    console.log(input);
  }

  //form action="The action attribute specifies where to send the form-data when a form is submitted.">
  return (
    <div>
      <form id="form" onSubmit={handleOnSubmit}>
        <label htmlFor="name">Name: </label>
        <input type="text" name="name" onChange={handleInput} />

        <label htmlFor="height_min">Min height: </label>
        <input type="number" name="height_min" onChange={handleInput} /><p>cms</p> <br />

        <label htmlFor="height_max">Max height: </label>
        <input type="number" name="height_max" onChange={handleInput} /><p>cms</p> <br />

        <label htmlFor="weight_min">Min weight: </label>
        <input type="number" name="weight_min" onChange={handleInput} />kgs <br />

        <label htmlFor="weight_max">Max weight: </label>
        <input type="number" name="weight_max" onChange={handleInput} />kgs <br />

        <label htmlFor="life_span">Life span: </label>
        <input
          type="text"
          name="life_span"
          placeholder="12-15"
          onChange={handleInput}
        />

        <label htmlFor="temperament">Choose temperaments: </label>
        <Temperaments name="temperament" onClick={handleInputTemps} />

        <label htmlFor="image">Image: </label>

        <input type="file" name="image" />

        <input
          type="text"
          name="image"
          placeholder="image url / link"
          onChange={handleInput}
        />

        <button type="submit">Create!</button>
      </form>
    </div>
  );
};
export default CreateDog;
