import Dog from "../Dog/Dog";
//import { Link } from "react-router-dom";

const Dogs = ({ dogs, loading }) => {
  // useEffect(() => {
  //   window.scrollTo(0, 0)
  // }, []);
  if (dogs.msg) {
    return (
      <div>
        <h1>dogs.msg</h1>
      </div>
    );
  } else {
    if (loading === 0) {
      return <h2> Los doggys est√°n cargando...</h2>;
    }

    return (
      <div >
        {dogs?.map((e, i) => {
          return (
            <div key={e.id} className="container">
              <Dog key={e.id} {...e} />
            </div>
          );
        })}
      </div>
    );
  }
};
export default Dogs;
