//import { fetchDogs } from "../actions/actions";

const initialState = {
  dogs: [],
  temperaments: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case "fetchDogs":
      return {
        ...state,
        dogs: action.payload,
      };
    case "createDog":
      return {
        ...state, //copio lo que ya tengo en el array le concateno el nuevo obj
        dogs: state.dogs.concat(action.payload), //[{},{}] --> [{},{},{}]
      };
    case "getTemperaments":
      return {
        ...state,
        temperaments: action.payload,
      };

    case "orderByName":
      const allDogs = state.dogs;
      
      const sortArr =
        action.payload === "asc"
          ? [...state.dogs].sort((a, b) => {
            console.log('holanda',sortArr)
              if (a.name > b.name) return -1;
              if (a.name < b.name) return 1;
              return 0;
            })
          : [...state.dogs].sort((a, b) => {
              if (a.name > b.name) return -1;
              if (a.name < b.name) return 1;
              return 0;
            });
            
      return {
        ...state,
        dogs: action.payload === "all" ? allDogs :sortArr
        
      };

    default:
      return state;
  }
};
export default rootReducer;
