import React, { useState } from "react";
import { useDispatch } from 'react-redux';
import { createDog }from '../../redux/actions/actions';
import Temperaments from '../Temperaments/Temperaments'

const CreateDog = () => {
  const [input, setInput] = useState({
    // name: "",
    // height_min: "",
    // height_max: "",
    // weight_min: "",
    // weight_max: "",
    // life_span: "",
    // temperament: "Happy",
    // image: "",
    name:"",
    height_min: "",
    height_max: "",
    weight_min: "",
    weight_max: "",
    life_span: "",
    temperament:["Happy", "Active", "Strong"]
  });

  function handleInput(e) {
    //onsole.log(e)
    e.preventDefault();
    setInput({
      ...input,
      [e.target.name]: e.target.value,
    });
  }
  const dispatch = useDispatch();

  // function handleSubmit(e){
  //     e.preventDefault();
  //     //console.log(e)
  //     dispatch(createDog(input));
  //     console.log(input);
  // }
  function handleOnSubmit(e) {
    e.preventDefault();
    console.log(input);
    dispatch(createDog(input));
    
  }



  //form action="The action attribute specifies where to send the form-data when a form is submitted.">
  return (
    <div>
      <form onSubmit={handleOnSubmit}>
        <label htmlFor="name">Name: </label>
        <input type="text" name="name" onChange={handleInput} />

        <label htmlFor=" height">Min height: </label>
        <input type="number" name="height_min" onChange={handleInput} />

        <label htmlFor="max height">Max height: </label>
        <input type="number" name="max height"  onChange={handleInput}/>

        <label htmlFor="min weight">Min weight: </label>
        <input type="number" name="min weight" onChange={handleInput} />

        <label htmlFor="max weight">Max weight: </label>
        <input type="number" name="max weight"onChange={handleInput} />

        <label htmlFor="life span">Life span: </label>
        <input type="text" name="life span" onChange={handleInput} />

        <label htmlFor="temperament">Temperament: </label>
        <Temperaments onChange={handleInput}/>

        <label htmlFor="image">Image: </label>

        <input type="file" name="image" />

        <input type="text" name="image" placeholder="image url / link" />

        <button type="submit">Create!</button>
      </form>
    </div>
  );
};
export default CreateDog;
