import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchDogs } from "../../redux/actions/actions";
import { getTemperaments } from "../../redux/actions/actions";

const Filters = () => {

  const dogs =  useSelector((state) => state.dogs);
  const temperaments = useSelector((state) => state.temperaments);
  const dispatch = useDispatch();



  useEffect(() => {
    dispatch(fetchDogs());
    dispatch(getTemperaments());
  }, [dispatch]);
  console.log(temperaments);
  console.log(temperaments);

// dogs.sort((a, b)=>{
// 	const nameA = a.name.toLowerCase();
//   const nameB = b.name.toLowerCase();
  
//   if( nameA < nameB) return -1;
// 	if(nameA > nameB) return 1;
//   return 0;
// }


  if (temperaments.length) {
    return (
      <div>
        <label htmlFor="">Order by name: </label>
        <select>
          <option value="A-Z"> A-Z </option>
          <option value="Z-A"> Z-A </option>
        </select>
        <label htmlFor=""> Order by weigth: </label>
        <select>
          <option value="weight_min"> Min weight </option>
          <option value="weight_max"> Max weight </option>
        </select><br /> <br />
        <label htmlFor=""> Filter by origin: </label>
        <select>
          <option value="Dogs from API"> Dogs from API </option>
          <option value="Dogs from DB"> Dogs created </option>
        </select>{" "}
        <br /> <br />
        <label htmlFor="">Filter by temperament: </label>
        <br /> <br />
        {temperaments.map((t) => {
          return (
            <>
              <label htmlFor={t}> {t} </label>
              <input type="checkbox" name={t} />
            </>
          );
        })}
      </div>
    );
  } else return <h2>Cargando...</h2>;
};
export default Filters;
