import React from "react";
import s from './SearchBar.module.css'
import { useState } from "react";
import { useDispatch } from "react-redux";
import { searchADog } from "../../redux/actions/actions";
import {FiSearch}  from "react-icons/fi";
const SearchBar = () => {
  const [inputSearch, setInputSearch] = useState("");
  const dispatch = useDispatch();

  function searchDog(e) {
    setInputSearch(e.target.value);
  } 
  function search(e) {
    dispatch(searchADog(inputSearch));
  }
  return (
    <div className={s.container}>
      <label htmlFor="search"  className={s.label}>Search breed / dog:</label>
      <input type="text" placeholder=" name..." onChange={searchDog} className={s.inputSearch} name="search" />
      <input type="submit" value={"   "} onClick={search} className={s.inputSend}/>
      <FiSearch className={s.icon} type="submit"/>
      
    </div>
  );
};
export default SearchBar;
