import { Route, Routes } from "react-router-dom";
import React, { useEffect, useState } from "react";
import Nav from "./components/Nav/Nav";
import Dogs from "./components/Dogs/Dogs";
import Pagination from "./components/Pagination/Pagination";
import "./App.css";

function App() {
  const [dogs, setDogs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [dogsPerPage] = useState(8);
  //const [dogsPerPage, setDogsPerPage] = useState(8);

  useEffect(() => {
    const fetchDogs = async () => {
      setLoading(true);
      const res = await fetch("http://localhost:3001/dogs").then( (d) => d.json() );
      setDogs(res);
      setLoading(false);
    };
    fetchDogs();
  }, []);

  //Get current dogs:
  const indexOfLastDog = currentPage * dogsPerPage; //8
  //console.log(indexOfLastDog);
  const indexOfFirstDog = indexOfLastDog - dogsPerPage; //0
  //console.log(indexOfFirstDog);
  const currentDogs = dogs.slice(indexOfFirstDog, indexOfLastDog); //8 objetos
  //console.log(currentDogs);

  //Change Page
  let paginate = (number) => setCurrentPage(number);

  return (
    <div className="App">
      <Routes>
        <Route path="/" element={<Nav />} />

        <Route
          path="/home"
          element={
            <>
              <Dogs dogs={currentDogs} loading={loading} />
              <Pagination
                dogsPerPage={dogsPerPage}
                totalDogs={dogs.length}
                paginate={paginate}
              />
            </>
          }
        />
      </Routes>
    </div>
  );
}

export default App;
