import React from "react";
import Dogs from "../Dogs/Dogs";
import Pagination from "../Pagination/Pagination";
import { fetchDogs } from "../../redux/actions/actions";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { useEffect, useState } from "react";
import Filters from "../Filters/Filters";
import { Link } from "react-router-dom";
import SearchBar from "../SearchBar/SearchBar";
const Home = () => {
  //const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [dogsPerPage] = useState(8);
  //const [dogsPerPage, setDogsPerPage] = useState(8);
  let dispatch = useDispatch();
  let dogs = useSelector((state) => state.dogs);

  //console.log(dogs);

  useEffect(() => {
    dispatch(fetchDogs());
  }, [dispatch]);

  if (dogs.msg) {
    return (
      <div>
        <h2>DOG NOT FOUND</h2>
        <Link to=''><button>Return home</button></Link>
      </div>
    );
  } else {
    //Get current dogs:
    const indexOfLastDog = currentPage * dogsPerPage; //8
    //console.log(indexOfLastDog);
    const indexOfFirstDog = indexOfLastDog - dogsPerPage; //0
    //console.log(indexOfFirstDog);
    const currentDogs = dogs.slice(indexOfFirstDog, indexOfLastDog); //8 objetos
    //console.log(currentDogs);

    //Change Page
    let paginate = (number) => setCurrentPage(number);

    return (
      <div>
        <div>
          <SearchBar />
        </div>
        <div>
          <Filters />
        </div>
        <div>
          {" "}
          <br />
          <Link to="/dog">
            <button>Create new dog!</button>
          </Link>
        </div>
        <Dogs dogs={currentDogs} loading={dogs.length} />
        <Pagination
          dogsPerPage={dogsPerPage}
          totalDogs={dogs.length}
          paginate={paginate}
        />
      </div>
    );
  }
};

export default Home;
