import React from "react";
import { useState } from "react";
import { useDispatch } from "react-redux";
import { searchADog } from "../../redux/actions/actions";

const SearchBar = () => {
const [inputSearch, setInputSearch] = useState("");
const dispatch = useDispatch();

function searchDog(e){
    setInputSearch(e.target.value);
}
function search(e){
    dispatch(searchADog(inputSearch))
}
    return(
        <div>
           <label htmlFor="search">Search breed / dog </label>
           <input type="text" placeholder="name..." onChange={searchDog}/>
            <input type="submit" value='search dog' onClic={search} />
        </div>
    )
};
export default SearchBar;
