//import { fetchDogs } from "../actions/actions";

const initialState = {
  dogs: [],
  temperaments: [],
  origin: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case "fetchDogs":
      return {
        ...state,
        dogs: action.payload,
      };
    case "createDog":
      return {
        ...state, //copio lo que ya tengo en el array le concateno el nuevo obj
        dogs: state.dogs.concat(action.payload), //[{},{}] --> [{},{},{}]
      };
    case "getTemperaments":
      return {
        ...state,
        temperaments: action.payload,
      };

    case "orderByName":
      const sortArr =
        action.payload === "asc"
          ? [...state.dogs].sort((a, b) => {
              if (a.name < b.name) return -1;
              if (a.name > b.name) return 1;
              return 0;
            })
          : [...state.dogs].sort((a, b) => {
              if (a.name > b.name) return -1;
              if (a.name < b.name) return 1;
              return 0;
            });

      return {
        ...state,
        dogs: sortArr,
      };

    case "orderByWeight":
      const sortArr2 =
        action.payload === "wmin"
          ? [...state.dogs].sort((a, b) => {
              if (a.weight_min < b.weight_min) return -1;
              if (a.weight_min > b.weight_min) return 1;
              return 0;
            })
          : [...state.dogs].sort((a, b) => {
              if (a.weight_max > b.weight_max) return -1;
              if (a.weight_max < b.weight_max) return 1;
              return 0;
            });

      return {
        ...state,
        dogs: sortArr2,
      };

      case "orderByOrigin":
        const origin = []
          
        return {
          ...state,
          dogs: origin,
        };
    default:
      return state;
  }
};
export default rootReducer;
