import { fetchDogs } from "../actions/actions";

const initialState = {
  dogs: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case "fetchDogs":
      return {
        ...state,
        dogs: action.payload,
      };
    case "createDog":
      return {
        ...state,
        
      };
    default:
      return state;
  }
};
export default rootReducer;
